    return {
        cls: 'i-'+ this.type + (this.icoClass ? ' ' + this.icoClass : '')
    }
  },
   mounted(){
    this.setSvgPrevent();
    this.setStyle();
  },
   methods: {
       setSvgPrevent(){
           if(this.$el.querySelector('.svg-prevent')) return;
           let svgprevent = document.createElement('span')
           svgprevent.classList.add('svg-prevent')
           this.$el.prepend(svgprevent)
       },
       setStyle(){
           let item = this.$el.querySelector('svg') ? this.$el.querySelector('svg') : this.$el.querySelector('img')
           const w = item.getAttribute('width')
           const h = item.getAttribute('height')
           this.$el.style.width = rem(w);
           this.$el.style.height = rem(h);
           function rem(value){
               return (value / 28) * 1.82 + 'rem'
           }
       }
   }  
}
</script>
